// Exact equations of Bring's curve
R1<X,Y,Z,T> := PolynomialRing(Rationals(), 4);
Q := X^2+Y^2+Z^2+T^2+(X+Y+Z+T)^2;
C := X^3+Y^3+Z^3+T^3-(X+Y+Z+T)^3;
Wgt, J1 :=  InvariantsGenus4Curves(Q, C : normalize := true);

// An approximation (up to 10^-9) of Bring's curve
K<I> := ComplexField();
R2<X,Y,Z,T> := PolynomialRing(K, 4);
Q2 := -0.696204170879482*X^2 - 8.92722674847333*I*X^2 - 50.7464824844223*X*Y + 58.7433272900886*I*X*Y - 57.8475326042462*X*Z - 32.3114052362709*I*X*Z + 0.696204169859874*X*T + 8.92722674924129*I*X*T + 47.4047605707472*Y^2 - 101.593270607375*I*Y^2 + 5.27347385941716e-10*Y*Z + 2.16410098974995e-10*I*Y*Z + 3.89862714549051*Y*T + 49.9909796383706*I*Y*T + 57.8475326039864*Z^2 + 32.311405236749*I*Z^2 + 57.8475326042301*Z*T + 32.3114052362822*I*Z*T + 1.25310940216974*T^2 + 16.0682630702241*I*T^2;
C2 := -234.90098969853*X^3 + 18.3191324015837*I*X^3 + 731.606628191784*X^2*Y + 478.455421156368*I*X^2*Y - 28.0203433984233*X^2*Z + 537.696155252943*I*X^2*Z + 352.35148455507*X^2*T - 27.4786985925642*I*X^2*T - 1147.27882034153*X*Y^2 - 485.876613084882*I*X*Y^2 + 562.593434153083*X*Y*Z - 32.1564704803837*I*X*Y*Z + 980.788369655334*X*Y*T - 92.8898492454102*I*X*Y*T + 28.0203433999316*X*Z^2 - 537.696155256996*I*X*Z^2 + 46.4622057542801*X*Z*T - 1058.24383026716*I*X*Z*T - 117.49502119373*X*T^2 + 9.16303864839564*I*X*T^2 + 125.61555530265*Y^3 - 446.88807153401*I*Y^3 + 6.75526150060275e-9*Y^2*Z - 1.19815129405316e-9*I*Y^2*Z + 26.7719595933429*Y^2*T + 14.3136325339934*I*Y^2*T - 562.593434151639*Y*Z^2 + 32.1564704772876*I*Y*Z^2 - 562.593434153289*Y*Z*T + 32.1564704813713*I*Y*Z*T - 537.802876178166*Y*T^2 + 41.9414243755401*I*Y*T^2 + 2.42764898083213e-9*Z^3 + 3.72099942130995e-9*I*Z^3 - 18.4418623558656*Z^2*T + 520.547675015364*I*Z^2*T - 18.4418623559262*Z*T^2 + 520.547675014923*I*Z*T^2 + 0.0222631708007216*T^3 - 0.00173622441208977*I*T^3;
Wgt, J2 := InvariantsGenus4Curves(Q2, C2 : normalize := true);

IsIsomorphic(Q1, C1, Q2, C2 : epsilon := 10^(-20));

// Here, we check that indeed, the invariants are the same up to a small precision
for i in [1..#J1] do
	ComplexField()!J1[i]-J2[i];
end for;
